fval.value:
package com.vordel.training

import com.vordel.circuit.Message
import com.vordel.circuit.oauth.token.OAuth2AccessToken

boolean invoke(Message msg) {
	OAuth2AccessToken token = msg.get("accesstoken");
	String jwt = msg.get("jwt.body");

	token.setValue(jwt);
	token.setAdditionalInformation("issued_token_type", "urn:ietf:params:oauth:token-type:jwt");

	return true;
}
